import React, { useEffect, useState } from 'react';
import { getPetDetails } from '../../api/petfinder';
import Hero from '../../components/hero';

// Import useParams
import { Link, Outlet, useParams } from 'react-router-dom';
// Import Navigate
import { Navigate } from 'react-router-dom';
import PetDetailsNotFound from '../../pages/petDetailsNotFound';

const HeroPhotos = () => {
  const [data, setData] = useState();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(false);
  const {id} = useParams(); // <--- Update me!
  const[photosResult, setPhotosResult] = useState([]);

  useEffect(() => {
    async function getPetsData() {
      try {
        const petsData = await getPetDetails(id);
        setData(petsData);
        setError(false);
      } catch (e) {
        setError(true);
      }
      setLoading(false);
    }

    getPetsData();
  }, [id]);

  if(data.photos.length > 0) {
    let arr = [];
    for(let i = 0; i < data.photos.length; i++) {
        arr.push(
            <article>
            <div className="pet-image-container">
              <img
                className="pet-image"
                src={
                  data.photos[i]?.medium || 'https://i.imgur.com/aEcJUFK.png'
                }
                alt=""
              />
            </div>
        </article>
        )
    }
  }
  console.log(data);

  return (
    <div>
      {loading ? (
        <h3>Loading...</h3>
      ) : error ? (
        <div>
          {<PetDetailsNotFound/>}
        </div>
      ) : (
        <article>
            <div className="pet-image-container">
              <img
                className="pet-image"
                src={
                  data.photos[0]?.medium || 'https://i.imgur.com/aEcJUFK.png'
                }
                alt=""
              />
            </div>
        </article>
  )
}
</div>)
};

export default HeroPhotos;
